import * as axios from "axios";


const instance = axios.create({
    withCredentials: true,
    baseURL: `https://social-network.samuraijs.com/api/1.0/`,
    headers: {
        'API-KEY': '51f8c99b-3e8e-46ff-b19a-76ebd4b525e5'
    }
})

export const usersAPI = {
    getUsers(currentPage, pageSize) {
        return (
            instance.get(`users?page=${currentPage}&count=${pageSize}`)
                .then(response => response.data)
        )
    },

}

export const unfollowUsers = (idUser) => {
    return (
        axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${idUser}`, {
            withCredentials: true,
            headers: {
                'API-KEY': '51f8c99b-3e8e-46ff-b19a-76ebd4b525e5'
            }

        })
    )
}
export const followUsers = (idUser) => {
    return (
        axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${idUser}`, {}, {
            withCredentials: true,
            headers: {
                'API-KEY': '51f8c99b-3e8e-46ff-b19a-76ebd4b525e5'
            }
        })
    )
}


