import React from "react";
import Preloader from "../../common/preloader/preloader";
import s from "./ProfileInfo.module.css";

class ProfileStatus extends React.Component {

  statusInputRef = React.createRef();

  state = {
    editMode: false
  };

  activeteEditMode = () => {
    debugger;
    console.log('this', this)
    this.setState({
      editMode: true
    })
  }
  deActiveteEditMode = () => {
    this.setState({
      editMode: false
    });
    this.props.updateStatus(this.statusInputRef.current.value);
  }

  render() {
    return (
      <div>
        {!this.state.editMode && (
          <div>
            <span onDoubleClick={ this.activeteEditMode}>{this.props.status}</span>
          </div>
        )}
        {this.state.editMode && (
          <div>
            <input ref={this.statusInputRef} autoFocus={true} onBlur={this.deActiveteEditMode} value={this.props.status}></input>
          </div>
        )}
      </div>
    );
  }
}

export default ProfileStatus;
