import React from 'react'
import { connect } from 'react-redux';
import {followAC, unfollowAC, setUsersAC, setCurrentPageAC, setUsersTotalCountAC} from './../../redux/usersRedecer';
import * as axios from "axios";
import Users from "./Users";
import preloader from "./../../assets/img/25.svg"

class UsersContainer extends React.Component {    
    
    componentDidMount(){
        this.props.toggleIsFetching (true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
        
        this.props.setUsers(response.data.items);
        this.props.setTotalUsersCount(response.data.totalCount)
            });  
    }
    onPageChanged = (pageNumber) =>{
        this.props.selectedPageActive(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {this.props.setUsers(response.data.items);
            })
    }
    

    render(){ 
       return <div>
           {this.props.isFetching ? <img src={preloader}/> : null}
       <Users 
       
       totalUsersCount={this.props.totalUsersCount} 
       pageSize={this.props.pageSize} 
       currentPage={this.props.currentPage}
       onPageChanged={this.onPageChanged}
       users={this.props.users}
       followUsers={this.props.followUsers}
       unFollowUsers={this.props.unFollowUsers}
       /> 
       </div>
    }
}

const mapStateToProps =(state)=>{
    return{
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching
    }
}

const mapDispatchToProps =(dispatch)=>{
    return{
        followUsers: (userId) => {
            dispatch(followAC(userId))
        },
        unFollowUsers: (userId) => {
            dispatch(unfollowAC(userId))
        },
        setUsers: (userId) => {
            dispatch(setUsersAC(userId))
        },
        selectedPageActive: (pageNumber) =>{
            dispatch(setCurrentPageAC(pageNumber))
        },
        setTotalUsersCount: (totalCount) =>{
            dispatch(setUsersTotalCountAC(totalCount))
        },
        toggleIsFetching:(isFetching) =>{
            dispatch(toggleIsFetchingAC(isFetching))
        }
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer);
