const SET_USER_DATA = 'SET_USER_DATA';

 
let initialState = {
    users: null,
    emails: null,
    login: null
    // isFetching: true
}

const authReducer = (state = initialState, action) => {

    switch (action.type) {  
        
        case SET_USER_DATA:
            return {
                ...state,
                ...action.data
            }
        default:
            return state;
    };
  }


export const setAuthUserData = (users,emails,login) => {
    return {
        type: SET_USER_DATA,
        data:{
          users,
          emails,
          login
        }        
    }
}
export default authReducer;
