const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SETUSERS';

let initialState = {
    users: [
    //     { followed: false, fullName: "Eugene",  id: 1, status: "i am a boss",
    //     location: {
    //         city:"Minsk",
    //         country: "Belarus"
    //     } 
    // },
    //     { followed: false, fullName: "Misha", id: 2, status: "i am a Russian",
    //     location: {
    //         city:"Moscow",
    //         country: "Russia"
    //     } 
    // },
    //     { followed: true, fullName: "Taras", id: 3, status: "i am from in Kiev",
    //     location: {
    //         city:"Kiev",
    //         country: "Ukraine"
    //     }     
    // },        
    //     { followed: true, fullName: "Polo", id: 4, status: "i am from in Poland",
    //     location: {
    //         city:"Warshawa",
    //         country: "Poland"
    //     }       
    // }        
    ]
}

const usersReducer = (state = initialState, action) => {

    switch (action.type) {

        case FOLLOW:

            return {
                ...state,
                users: state.users.map( u => {
                    if (u.id === action.userId){
                        return{...u, followed: true}
                    }
                    return u;
                })
            }

        case UNFOLLOW:

            return {
                ...state,
                users: state.users.map( u => {
                    if (u.id === action.userId){
                        return{...u, followed: false}
                    }
                    return u;
                })
            }

        case SET_USERS:
            return{...state, users:[ ...state.users, action.users ] }     

        default:
            return state;
    };
}


export const followAC = (userId) => {
    return {
        type: FOLLOW,
        userId
    }
}
export const unfollowAC = (userId) => {
    return {
        type: UNFOLLOW,
        userId
    }
}
export const setUsersAC = (userId) => {
    return {
        type: SET_USERS,
        userId
    }
}

export default usersReducer;
